{* simple navigation *}
{* note, function can only be defined once *}
{*
  variables:
  node: contains the current node.
  aclass: is used to build a string containing class names given to the a tag if one is used
  liclass: is used to build a string containing class names given to the li tag.
*}

{function name=Nav_menu depth=1}{strip}
&lt;ul&gt;
  {foreach $data as $node}
    {* setup classes for the anchor and list item *}
    {$liclass='menudepth'|cat:$depth}
    {$aclass=''}

    {* the first child gets a special class *}
    {if $node@first &amp;&amp; $node@total &gt; 1}{$liclass=$liclass|cat:' first_child'}{/if}

    {* the last child gets a special class *}
    {if $node@last &amp;&amp; $node@total &gt; 1}{$liclass=$liclass|cat:' last_child'}{/if}

    {if $node-&gt;current}
      {* this is the current page *}
      {$liclass=$liclass|cat:' menuactive'}
      {$aclass=$aclass|cat:' menuactive'}
    {/if}

    {if $node-&gt;parent}
      {* this is a parent of the current page *}
      {$liclass=$liclass|cat:' menuactive menuparent'}
      {$aclass=$aclass|cat:' menuactive menuparent'}
    {/if}

    {if $node-&gt;children_exist}
      {$liclass=$liclass|cat:' parent'}
      {$aclass=$aclass|cat:' parent'}
    {/if}

    {* build the menu item node *}
    {if $node-&gt;type == 'sectionheader'}
      &lt;li class='sectionheader {$liclass}'&gt;&lt;span&gt;{$node-&gt;menutext}&lt;/span&gt;
        {if isset($node-&gt;children)}
          {Nav_menu data=$node-&gt;children depth=$depth+1}
        {/if}
      &lt;/li&gt;
    {else if $node-&gt;type == 'separator'}
      &lt;li class='separator {$liclass}'&gt;&lt;hr class='separator'/&gt;&lt;/li&gt;
    {else}
      {* regular item *}
      &lt;li class="{$liclass}"&gt;
        &lt;a class="{$aclass}" href="{$node-&gt;url}"{if $node-&gt;target ne ""} target="{$node-&gt;target}"{/if}&gt;&lt;span&gt;{$node-&gt;menutext}&lt;/span&gt;&lt;/a&gt;
        {if isset($node-&gt;children)}
          {Nav_menu data=$node-&gt;children depth=$depth+1}
        {/if}
      &lt;/li&gt;
    {/if}
  {/foreach}
&lt;/ul&gt;
{/strip}{/function}

{if isset($nodes)}
{Nav_menu data=$nodes depth=0}
{/if}

{* default breadcrumbs template *}
{strip}
&lt;div class="breadcrumb"&gt;
  {if isset($starttext)}{$starttext}:&amp;nbsp;{/if}
  {foreach $nodelist as $node}
    {$spanclass='breadcrumb'}
    {if $node-&gt;current}
      {$spanclass=$spanclass|cat:' current'}
    {/if}

    &lt;span class="{$spanclass}"&gt;
      {if $node@last}
        {$node-&gt;menutext}
      {elseif $node-&gt;type == 'sectionheader'}
        {$node-&gt;menutext}&amp;nbsp;
      {else}
        &lt;a href="{$node-&gt;url}" title="{$node-&gt;menutext}"&gt;{$node-&gt;menutext}&lt;/a&gt;
      {/if}
    &lt;/span&gt;

    {if !$node@last}&amp;raquo;&amp;nbsp;{/if}
  {/foreach}
&lt;/div&gt;
{/strip}

{* cssmenu *}
{* this template uses recursion, but not a smarty function. *}
{*
  variables:
  node: contains the current node.
  aclass: is used to build a string containing class names given to the a tag if one is used
  liclass: is used to build a string containing class names given to the li tag.
*}
{if !isset($depth)}{$depth=0}{/if}
{strip}

{if $depth == 0}
&lt;div id="menuwrapper"&gt;
&lt;ul id="primary-nav"&gt;
{else}
&lt;ul class="unli"&gt;
{/if}

{$depth=$depth+1}
{foreach $nodes as $node}
  {* setup classes for the anchor and list item *}
  {$liclass=[]}
  {$aclass=[]}

  {* the first child gets a special class *}
  {if $node@first &amp;&amp; $node@total &gt; 1}{$liclass[]='first_child'}{/if}

  {* the last child gets a special class *}
  {if $node@last &amp;&amp; $node@total &gt; 1}{$liclass[]='last_child'}{/if}

  {if $node-&gt;current}
    {* this is the current page *}
    {$liclass[]='menuactive'}
    {$aclass[]='menuactive'}
  {/if}
  {if $node-&gt;has_children}
    {* this is a parent page *}
    {$liclass[]='menuparent'}
    {$aclass[]='menuparent'}
  {/if}
  {if $node-&gt;parent}
    {* this is a parent of the current page *}
    {$liclass[]='menuactive'}
    {$aclass[]='menuactive'}
  {/if}

  {* build the menu item from the node *}
  {if $node-&gt;type == 'sectionheader'}
    &lt;li class='{implode(' ',$liclass)}'&gt;&lt;a{if count($aclass) &gt; 0} class="{implode(' ',$aclass)}"{/if}&gt;&lt;span class="sectionheader"&gt;{$node-&gt;menutext}&lt;/span&gt;&lt;/a&gt;
      {if isset($node-&gt;children)}
        {include file=$smarty.template nodes=$node-&gt;children}
      {/if}
    &lt;/li&gt;
  {else if $node-&gt;type == 'separator'}
    &lt;li style="list-style-type: none;"&gt;&lt;hr class="menu_separator"/&gt;&lt;/li&gt;
  {else}
    {* regular item *}
    &lt;li class="{implode(' ',$liclass)}"&gt;
      &lt;a{if count($aclass) &gt; 0} class="{implode(' ',$aclass)}"{/if} href="{$node-&gt;url}"{if $node-&gt;target ne ""} target="{$node-&gt;target}"{/if}&gt;&lt;span&gt;{$node-&gt;menutext}&lt;/span&gt;&lt;/a&gt;
      {if isset($node-&gt;children)}
        {include file=$smarty.template nodes=$node-&gt;children}
      {/if}
    &lt;/li&gt;
  {/if}
{/foreach}
{$depth=$depth-1}
&lt;/ul&gt;

{if $depth == 0}
&lt;div class="clearb"&gt;&lt;/div&gt;
&lt;/div&gt;{* menuwrapper *}
{/if}
{/strip}

{* cssmenu_ulshadow navigation *}
{* note, function can only be defined once *}
{*
  variables:
  node: contains the current node.
  aclass: is used to build a string containing class names given to the a tag if one is used
  liclass: is used to build a string containing class names given to the li tag.
*}

{function name=cssmenu_ulshadow depth=1}
&lt;ul{if $depth ==0} id="primary-nav"{else} class="unli"{/if}&gt;
  {foreach $data as $node}
    {* setup classes for the anchor and list item *}
    {$liclass=''}
    {*{$liclass=' depth'|cat:$depth}*}
    {$aclass=''}

    {* the first child gets a special class
    {if $node@first &amp;&amp; $node@total &gt; 1}{$liclass=$liclass|cat:' first_child'}{/if}
    *}

    {* the last child gets a special class
    {if $node@last &amp;&amp; $node@total &gt; 1}{$liclass=$liclass|cat:' last_child'}{/if}
    *}

    {if $node-&gt;current}
      {* this is the current page *}
      {$liclass=$liclass|cat:' menuactive'}
      {$aclass=$aclass|cat:' menuactive'}
    {else if $node-&gt;parent}
      {* this is a parent of the current page *}
      {$liclass=$liclass|cat:' parent'}
      {$aclass=$aclass|cat:' parent'}
    {/if}
    {if isset($node-&gt;children)}
      {$liclass=$liclass|cat:' menuparent'}
      {$aclass=$aclass|cat:' menuparent'}
    {/if}

    {* build the menu item node *}
    {if $node-&gt;type == 'sectionheader'}
      &lt;li class='sectionheader {$liclass}'&gt;&lt;span&gt;{$node-&gt;menutext}&lt;/span&gt;
        {if isset($node-&gt;children)}
          {cssmenu_ulshadow data=$node-&gt;children depth=$depth+1}
        {/if}
      &lt;/li&gt;
    {else if $node-&gt;type == 'separator'}
      &lt;li class='separator {$liclass}'&gt;&lt;hr class='separator'/&gt;&lt;/li&gt;
    {else}
      {* regular item *}
      &lt;li class="{$liclass}"&gt;
        &lt;a class="{$aclass}" href="{$node-&gt;url}"{if $node-&gt;target ne ""} target="{$node-&gt;target}"{/if}&gt;&lt;span&gt;{$node-&gt;menutext}&lt;/span&gt;&lt;/a&gt;
        {if isset($node-&gt;children)}
          {cssmenu_ulshadow data=$node-&gt;children depth=$depth+1}
        {/if}
      &lt;/li&gt;
    {/if}
  {/foreach}
  {if $depth &gt; 0}
    &lt;li class="separator once" style="list-style-type: none;"&gt;&amp;nbsp;&lt;/li&gt;
  {/if}
&lt;/ul&gt;
{/function}

{if isset($nodes)}
&lt;div id="menuwrapper"&gt;
  {cssmenu_ulshadow data=$nodes depth=0}
  &lt;div class="clearb"&gt;&lt;/div&gt;
&lt;/div&gt;
{/if}

{* minimal navigation *}
{*
  variables:
  node: contains the current node.
  aclass: is used to build a string containing class names given to the a tag if one is used
  liclass: is used to build a string containing class names given to the li tag.
*}
{* CSS classes used in this template:
.currentpage - The active/current page
.bullet_sectionheader - To style section header
hr.separator - To style the ruler for the separator *}

{if !isset($depth)}{$depth=0}{/if}

{if isset($nodes)}{strip}
&lt;ul&gt;
  {foreach $nodes as $node}
    {if $node-&gt;type == 'sectionheader'}
      {* section header *}
      &lt;li class="sectionheader{if $node-&gt;parent} activeparent{/if}"&gt;
        {$node-&gt;menutext}
        {if isset($node-&gt;children)}
          {include file=$smarty.template nodes=$node-&gt;children depth=$depth+1}
        {/if}
      &lt;/li&gt;
    {else if $node-&gt;type == 'separator'}
      &lt;li style="list-style-type: none;"&gt;&lt;hr class="separator"/&gt;&lt;/li&gt;
    {else}
      {* regular item *}
      {$liclass=''}
      {$aclass=''}
      {if $node-&gt;current}
        {$liclass='currentpage'}
        {$aclass='currentpage'}
      {elseif $node-&gt;parent}
        {$liclass='activeparent'}
        {$aclass='activeparent'}
      {/if}
      &lt;li{if $liclass != ''} class="{$liclass}"{/if}&gt;
        &lt;a{if $aclass !=''} class="{$aclass}"{/if} href="{$node-&gt;url}"{if $node-&gt;target ne ""} target="{$node-&gt;target}"{/if}&gt;{$node-&gt;menutext}&lt;/a&gt;
        {if isset($node-&gt;children)}
          {include file=$smarty.template nodes=$node-&gt;children depth=$depth+1}
        {/if}
      &lt;/li&gt;
    {/if}
  {/foreach}
&lt;/ul&gt;
{/strip}{/if}

{strip}

{$main_id = ' id=\'main-menu\''}
{function do_class}
    {if count($classes) &gt; 0} class='{implode(' ',$classes)}'{/if}
{/function}

{function name='Simplex_menu' depth='1'}
    &lt;ul{$main_id}{if isset($ul_class) &amp;&amp; $ul_class != ''} class="{$ul_class}"{/if}&gt;
        {$main_id = ''}
        {$ul_class = ''}
        {foreach $data as $node}
            {* setup classes for the anchor and list item *}
            {$list_class = []}
            {$href_class = ['cf']}
            {$parent_indicator = ''}
            {$aria_support = ''}

            {if $node-&gt;current || $node-&gt;parent}
                {* this is the current page *}
                {$list_class[] = 'current'}
                {$href_class[] = 'current'}
            {/if}

            {if $node-&gt;children_exist}
                {$list_class[] = 'parent'}
                {$aria_support = ' aria-haspopup=\'true\''}
                {$parent_indicator = ' &lt;i class=\'icon-arrow-left\' aria-hidden=\'true\'&gt;&lt;/i&gt;'}
            {/if}

            {* build the menu item node *}
            {if $node-&gt;type == 'sectionheader'}
                {$list_class[] = 'sectionheader'}
                &lt;li{do_class classes=$list_class}{$aria_support}&gt;&lt;span&gt;{$node-&gt;menutext}{$parent_indicator}&lt;/span&gt;
                {if isset($node-&gt;children)}
                    {Simplex_menu data=$node-&gt;children depth=$depth+1}
                {/if}
                &lt;/li&gt;
            {else if $node-&gt;type == 'separator'}
                {$list_class[] = 'separator'}
                &lt;li{do_class classes=$list_class}'&gt;&lt;hr class='separator'/&gt;&lt;/li&gt;
            {else}
                {* regular item *}
                &lt;li{do_class classes=$list_class}{$aria_support}&gt;
                    &lt;a{do_class classes=$href_class} href='{$node-&gt;url}'{if $node-&gt;target != ''} target='{$node-&gt;target}'{/if}&gt;{$node-&gt;menutext}{$parent_indicator}&lt;/a&gt;
                    {if isset($node-&gt;children)}
                        {Simplex_menu data=$node-&gt;children depth=$depth+1}
                    {/if}
                &lt;/li&gt;
            {/if}
        {/foreach}
    &lt;/ul&gt;
{/function}

{if isset($nodes)}
    {Simplex_menu data=$nodes depth='0' ul_class='cf'}
{/if}

{/strip}

{strip}

{$main_id = ' id=\'footer-menu\''}
{function do_footer_class}
    {if count($classes) &gt; 0} class='{implode(' ',$classes)}'{/if}
{/function}

{function name='Simplex_footer_menu' depth='1'}
    &lt;ul{$main_id}{if isset($ul_class) &amp;&amp; $ul_class != ''} class="{$ul_class}"{/if}&gt;
        {$main_id = ''}
        {$ul_class = ''}
        {foreach $data as $node}
            {* setup classes for the anchor and list item *}
            {$list_class = []}
            {$href_class = []}

            {if $node-&gt;current || $node-&gt;parent}
                {* this is the current page *}
                {$list_class[] = 'current'}
                {$href_class[] = 'current'}
            {/if}

            {if $node-&gt;children_exist}
                {$list_class[] = 'parent'}
            {/if}

            {* build the menu item node *}
            {if $node-&gt;type == 'sectionheader'}
                {$list_class[] = 'sectionheader'}
                &lt;li{do_footer_class classes=$list_class}&gt;&lt;span&gt;{$node-&gt;menutext}&lt;/span&gt;
                {if isset($node-&gt;children)}
                    {Simplex_footer_menu data=$node-&gt;children depth=$depth+1}
                {/if}
                &lt;/li&gt;
            {else if $node-&gt;type == 'separator'}
                {$list_class[] = 'separator'}
                &lt;li{do_footer_class classes=$list_class}'&gt;&lt;hr class='separator'/&gt;&lt;/li&gt;
            {else}
                {* regular item *}
                &lt;li{do_footer_class classes=$list_class}&gt;
                    &lt;a{do_footer_class classes=$href_class} href='{$node-&gt;url}'{if $node-&gt;target != ''} target='{$node-&gt;target}'{/if}&gt;{$node-&gt;menutext}&lt;/a&gt;
                    {if isset($node-&gt;children)}
                        {Simplex_footer_menu data=$node-&gt;children depth=$depth+1}
                    {/if}
                &lt;/li&gt;
            {/if}
        {/foreach}
    &lt;/ul&gt;
{/function}

{if isset($nodes)}
    {Simplex_footer_menu data=$nodes depth='0' ul_class='cf'}
{/if}

{/strip}

